import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';


class Square extends React.Component {

    render() {
      return (

        <button className="square" 

            //Ao clicar em qualquer quadradro, irá alterar o valor do "state" para "X"
            onClick ={
                () => this.props.onClick()
            }
        >
            {this.props.value}
        </button>
      );
    }
}





  class Board extends React.Component {


    // Instanciando um array de 9 indices e passando o valor "null" para os indices
    constructor (props) {
        super(props);
        this.state = {
            squares: Array(9).fill(null),
            xIsNext: true,
        };

    }


    handleClick(i){
        const squares = this.state.squares.slice();
        squares [i] = this.state.xIsNext ? "X" : "O";

        this.setState({
            squares: squares,
            xIsNext: !this.state.xIsNext,
        });
    }

    renderSquare(i) {
      return <Square 
        value={this.state.squares[i]}
        onClick ={()=> this.handleClick(i)} 
      />;
    }

    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>
          <div className="board-row">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="board-row">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="board-row">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
